mapper.select:
SELECT COUNT(itemNo)
  FROM CART
 WHERE USER_ID = #{userId}
   AND ITEM_NO = #{itemNo}
 ORDER BY REG_DT ASC

mapper.insert:
INSERT INTO CART (
       USER_ID
     , ITEM_NO
     , ITEM_AMOUNT
     , PRICE
     , SHIPPING
	)
VALUES (
       #{userId}
     , #{itemNo}
     , #{itemAmount}
     , #{price}
     , #{shipping}
	)

mapper.update:
UPDATE CART
   SET ITEM_AMOUNT = #{itemAmount}

     , PRICE = #{price}


     , SHIPPING = #{shipping}

 WHERE USER_ID = #{userId}
   AND ITEM_NO = #{itemNo}

mapper.delete:
DELETE FROM CART
 WHERE USER_ID = #{userId}
   AND ITEM_NO = #{itemNo}
